package operator;

public class assingop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		int c=a+=b;
		int d=a-=b;
		int e=a*=b;
		int f=a/=b;
		int g=a&=b;
		int h=a/=b;
		int i=a^=b;
		System.out.println("add:"+c);
		System.out.println("sub:"+d);
		System.out.println("mul:"+e);
		System.out.println("div:"+f);
		System.out.println("and=:"+g);
		System.out.println("or=:"+h);
		System.out.println("or=:"+i);
		
		

	}

}
