package operator;

public class conditionalop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		int b=30;
		int min;
		int max;
		min=(a<b)?a:b;
		max=(a>b)?a:b;
		System.out.println("max value is:"+min);
		System.out.println("max value is:"+max);

	}

}
