package Itrativestatment;

public class designpatterns {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j;
		for(i=1;i<=4;i++){
			for(j=1;j<=i;j++) {
				System.out.print("*");
			}
			System.out.println( );
		}
		for(i=1;i<=4;i++){
			for(j=3;j>=i;j--) {
				System.out.print("*");
			}System.out.println();
			
		}
		}

	}


