package operator;

public class shiftop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=a>>2;
		int c=(b);
		System.out.println("right shift of a:"+c);
		int a1=5;
		int b1=a1<<2;
		int c1=(b1);
		System.out.println("left shif of a1:"+c1);
		int a2=4;
		int b2=a2>>3;
		int c2=(b2);
		System.out.println("right shift of a2:"+c2);
		int a3=11;
		int b3=a3<<1;
		int c3=(b3);
		System.out.println("left shift of a3:"+c3);
		int a4=16;
		int b4=a<<4;
		int c4=(b4);
		System.out.println("right shift of a4:"+c4);

	}

}
